INSERT INTO `cms_template_list` (`id`, `createdAt`, `updatedAt`, `template_uuid`, `title`, `type`, `name`, `html`, `isd`, `isu`, `uuid`) VALUES (1, '2024-10-07 04:48:18', '2024-10-07 04:48:18', '1f8dda2a-6e9e-4762-a273-1bc138ec9ffa', '默认首页模板', 'index', 'default', '{% extends \"./inc_base.html\" %}\n<!-- 默认使用亮色logo -->\n{%set logo =\'light\'%}\n{%block body%}class=\"header-sticky header-over\"{%endblock%}\n{% block middle %}\n<!-- INTRO -->\n<div class=\"section bg-light position-relative overflow-hidden overlay-dark overlay-opacity-5 bg-cover jarallax\" \nstyle=\"background-image:url(\'/public/assets/demo.files/video/hd0936.jpg\')\" \ndata-speed=\"1.4\" \ndata-jarallax-video=\"mp4:/public/assets/demo.files/video/hd0936.mp4,webm:/public/assets/demo.files/video/hd0936.webm,ogv:/public/assets/demo.files/video/hd0936.ogv\">\n\n<div class=\"container position-relative z-index-2 d-middle\"> \n        \n    <div class=\"text-center text-white py-7\" data-aos=\"fade-in\" data-aos-delay=\"0\" data-aos-offset=\"0\">\n\n        <!-- main title -->\n        <h1 class=\"display-4 mb-0 fw-bold\">\n            CmsWing 演示\n        </h1>\n        \n        <!-- slogan -->\n        <p class=\"h5 fw-normal mb-0\">\n            简洁、直观、强悍的前端开发框架，让web开发更迅速、简单。\n        </p>\n\n        <!-- play button -->\n        <div class=\"clearfix mt-5\">\n            <span class=\"d-inline-block rounded-circle border border-white border-3 p-1 transition-hover-zoom transition-all-ease-150 shadow-warning-lg\">\n                <a href=\"/public/assets/demo.files/video/hd0936.mp4\" \n                    data-ajax-modal-type=\"video\" \n                    data-ajax-modal-size=\"modal-xl\". \n                    data-ajax-modal-centered=\"true\" \n                    class=\"js-ajax-modal d-inline-flex bg-white text-dark rounded-circle align-items-center justify-content-center text-decoration-none\" \n                    style=\"width:60px;height:60px;\">\n  <svg width=\"34\" height=\"34\" xmlns=\"http://www.w3.org/2000/svg\" fill=\"currentColor\" class=\"bi bi-play-fill\" viewBox=\"0 0 16 16\">  \n    <path d=\"m11.596 8.697-6.363 3.692c-.54.313-1.233-.066-1.233-.697V4.308c0-.63.692-1.01 1.233-.696l6.363 3.692a.802.802 0 0 1 0 1.393z\"></path>\n  </svg>\n                </a>\n            </span>\n\n            <small class=\"d-block mt-2\">\n                查看演示视频\n            </small>\n        </div>\n        <!-- /play button -->\n\n    </div>\n\n</div>\n\n<div class=\"absolute-full w-100 overflow-hidden opacity-5 z-index-1\">\n    <img class=\"img-fluid\" width=\"2000\" height=\"2000\" src=\"/public/assets/images/masks/shape-line-lense.svg\" alt=\"...\">\n</div>\n\n</div>	\n<!-- /INTRO -->\n	<!-- CALL TO ACTION -->\n    <div class=\"section py-5 position-relative mb-1\">\n        <div class=\"container position-relative z-index-1\">\n            <div class=\"row text-center-xs\">\n\n                <div class=\"col-12 col-md-8\">\n                    <h3 class=\"m-0\">响应式布局自动适配各种终端和场景!</h3>\n                    <p class=\"m-0\">简洁、直观、强悍的前端开发框架，让web开发更迅速、简单。</p>\n                </div>\n                \n                <div class=\"col-12 mt-4 d-block d-md-none\"><!-- mobile spacer --></div>\n\n                <div class=\"col-12 col-md-4 text-align-end\">\n                    <a href=\"https://gitee.com/cmswing/CmsWing.git\" target=\"_blank\" class=\"btn btn-pill btn-primary d-block-xs\">\n                        <b class=\"fw-medium\">CmsWing</b> / 获取\n                    </a>\n                </div>\n\n            </div>\n        </div>\n\n        <!-- svg shape -->\n        <svg class=\"absolute-full z-index-0\" width=\"100%\" height=\"100%\" viewBox=\"0 0 1920 90\" preserveAspectRatio=\"none\">\n            <path fill=\"rgba(55,125,255,.04)\" d=\"M1920,0C1217,0,120.574,155.567,0,0v90h1920V0z\"></path>\n        </svg>\n\n    </div>\n    <!-- /CALL TO ACTION -->\n<!-- blog news -->\n<div class=\"container\">\n<div class=\"mt-5\">\n\n    <!-- News -->\n    <h2 class=\"fw-bold mb-0\">\n       最新动态!\n    </h2>\n    <p class=\"fw-medium\">随时了解最新最热行业信息！</p>\n    {% set top = \'cms_doc\'|@findAll({\n        include : \'cms_classify\',\n        offset: 0,\n        limit: 1,\n        order:[[\'id\',\'desc\']]\n        })%}\n    <!-- sticky article -->\n    <div class=\"row mt-6\">\n        <a href=\"/cms/detail/{{top[0].id}}\" target=\"_blank\" class=\"col-12 col-md-4 mb-4\">\n            <img class=\"img-fluid w-100 rounded\"\n                src=\"{{top[0].cover_url}}\"\n                alt=\"...\">\n        </a>\n\n        <div class=\"col pb-4\">\n\n            <!-- title -->\n            <div class=\"mb-4\">\n\n                <h2 class=\"h4 mb-0\">\n                    <a href=\"/cms/detail/{{top[0].id}}\" class=\"text-dark fw-medium\">\n                        {{top[0].title}}\n                    </a>\n                </h2>\n\n                <!-- publish date -->\n                <span class=\"text-muted\">\n                    {{helper.moment(top[0].createdAt).fromNow()}}\n                </span>\n\n            </div>\n\n            <!-- content preview -->\n            <div class=\"mb-4\">\n                <p>{{top[0].description}}</p>\n            </div>\n\n            <a href=\"/cms/list/{{top[0].cms_classify.name or top[0].cms_classify.id}}\" target=\"_blank\" class=\"link-muted\">{{top[0].cms_classify.title}}</a>\n\n        </div>\n\n    </div>\n    <!-- /sticky article -->\n    <!-- more articles / blog -->\n    <div class=\"py-4 mt-3\">\n\n        <div class=\"row\">\n            {% set newlist = \'cms_doc\'|@findAll({\n                include : \'cms_classify\',\n                offset: 1,\n                limit: 4,\n                order:[[\'id\',\'desc\']]\n                })%}\n{% for items in newlist | batch(2) %}\n<div class=\"col-12 col-md-6\">\n     {% for item in items %}\n     <div class=\"d-flex mb-3 text-decoration-none\">\n\n        <!-- image -->\n        <div style=\"width:100px;height:100px\"\n            class=\"text-center bg-light rounded bg-cover lazy\"\n            data-background-image=\"{{item.cover_url}}\">\n        </div>\n\n        <div class=\"col px-3\">\n\n            <h3 class=\"h5 fw-bold\">\n                <a href=\"/cms/detail/{{item.id}}\" target=\"_blank\" class=\"text-dark\">\n                    {{item.title}}\n                </a>\n            </h3>\n\n            <p class=\"small\">\n                <strong>({{helper.moment(item.createdAt).fromNow()}})</strong>  {{item.description|truncate(50, true, \"...\")}}\n            </p>\n\n        </div>\n\n    </div>\n    {% endfor %}\n</div>\n            {% endfor %}\n       \n        </div>\n\n    </div>\n    <!-- /more articles / blog -->\n</div>\n</div>\n<!-- /blog news -->\n{% endblock %}', 1, 1, 'e90bba0b-d935-4c74-b9bd-48c89cb119d0');
INSERT INTO `cms_template_list` (`id`, `createdAt`, `updatedAt`, `template_uuid`, `title`, `type`, `name`, `html`, `isd`, `isu`, `uuid`) VALUES (2, '2024-10-07 04:48:18', '2024-10-07 04:48:18', '1f8dda2a-6e9e-4762-a273-1bc138ec9ffa', '默认列表模板', 'list', 'default', '{% extends \"./inc_base.html\" %}\n{%block body%}class=\"header-scroll-reveal\"{%endblock%}\n{% block middle %}\n<div class=\"section pt-4 pb-4\">\n    <div class=\"container\">\n        <!--\n\n	Color Schemas\n	.swiper-white (default)\n	.swiper-black\n	.swiper-dark\n	.swiper-primary\n	.swiper-danger\n	.swiper-success\n	.swiper-warning\n	.swiper-info\n	.swiper-pink\n	.swiper-indigo\n	.swiper-purple\n\n	Autoplay\n	\"autoplay\": false\n	\"autoplay\": { \"delay\" : 1500, \"disableOnInteraction\": false }\n\n-->\n        {% set position = \'cms_doc\'|@findAll({\n        include : \'cms_classify\',\n        where:{\n        classify_id:classify.id,\n        FIND_IN_SET:[\'position\',1]\n        },\n        offset: 0,\n        limit: 10\n        })%}\n        {%if position %}\n        <div class=\"swiper-container swiper-purple mb-4 d-none d-lg-block\" data-swiper=\'{\n		\"slidesPerView\": 3,\n		\"spaceBetween\": 8,\n		\"slidesPerGroup\": 1,\n		\"loop\": true,\n		\"autoplay\": { \"delay\" : 3000, \"disableOnInteraction\": false },\n		\"breakpoints\": {\n			\"1024\": { \"slidesPerView\": \"3\" },\n			\"920\":	{ \"slidesPerView\": \"2\" },\n			\"640\":	{ \"slidesPerView\": \"1\" },\n      \"300\":	{ \"slidesPerView\": \"1\" }\n		}\n	}\'>\n\n            <div class=\"hide swiper-wrapper\">\n                {%for item in position%}\n                <div class=\"swiper-slide\">\n                    <div class=\"card\">\n                        <div class=\"row g-0\">\n                            <div class=\"col-md-4\">\n                                {%if not item.cover_url%}\n                                <svg class=\"d-block w-100 rounded-start\" xmlns=\"http://www.w3.org/2000/svg\" role=\"img\"\n                                    style=\"text-anchor: middle; user-select: none ;height:104px\">\n                                    <rect width=\"100%\" height=\"100%\" fill=\"#c6d3e6\"></rect>\n                                    <text x=\"50%\" y=\"50%\" fill=\"#869ab8\" dy=\".2em\">\n                                        暂无图片\n                                    </text>\n                                </svg>\n                                {%else%}\n                                <img\n                  style=\"height:104px\"\n                    class=\"img-fluid rounded-start\"\n                    src=\"{{item.cover_url}}\"\n                    alt=\"...\"\n                  />\n                  {%endif%}\n                            </div>\n                            <div class=\"col-md-8\">\n                                <div class=\"card-body\">\n                                    <a href=\"/cms/detail/{{item.id}}\" class=\"card-title\"\n                                        target=\"_blank\">{{item.title|truncate(26, true, \"...\")}}</a>\n                                    <p class=\"card-text\">\n                                        <small class=\"text-muted\">{{item.cms_classify.title}}/{{helper.moment(item.createdAt).fromNow()}}</small>\n                                    </p>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n                </div>\n                {%endfor%}\n\n\n            </div>\n\n            <!-- Add Arrows -->\n            <div class=\"rounded-circle swiper-button-next\"></div>\n            <div class=\"rounded-circle swiper-button-prev\"></div>\n\n        </div>\n        {%endif%}\n        <div class=\"row\">\n            <div class=\"col-12 col-lg-10 order-2\">\n                <div class=\"shadow-xs bg-white mb-4 p-3 rounded clearfix\">\n                    <label class=\"h5 mb-0\"> {{classify.title}} </label>\n\n                    <div class=\"float-end position-relative mt-1 small\">\n                        排序: &nbsp;\n                        <a href=\"{{ orderby.def.url }}\" class=\"text-danger text-decoration-none\"\n                            data-bs-toggle=\"dropdown\" aria-expanded=\"false\">\n                            {{ orderby.def.name }}\n                            <i class=\"fi fi-arrow-down-slim smaller\"></i>\n                        </a>\n\n                        <ul class=\"dropdown-menu border-0 mt-3 rounded fs-6\">\n                            {% for item in orderby.list %}\n                            <li class=\"dropdown-item {% if ctx.query.orderby==item.id %}active{% endif %}\">\n                                <a href=\"{{ item.url }}\" class=\"text-muted small py-2 d-block\">{{ item.name }}</a>\n                            </li>\n                            {% endfor %}\n                        </ul>\n                    </div>\n                    <hr />\n                    <h1 class=\"h6 mb-0\">\n                        找到 <span class=\"text-danger\">{{ list.count }}</span> 个相关结果\n                    </h1>\n                </div>\n\n                {%for item in list.rows%}\n                <!-- item -->\n                <div class=\"shadow-xs rounded mb-3 p-3\">\n                    <div class=\"row g-0\">\n                        <div class=\"col-12 col-md-3 p--0\">\n                            <figure class=\"overflow-hidden text-center clearfix d-block m-0 position-relative\">\n                                <a href=\"/cms/detail/{{item.id}}\" class=\"text-decoration-none\" target=\"_blank\">\n                                    {%if not item.cover_url%}\n                                    <svg class=\"d-block w-100 fs-1 rounded\" xmlns=\"http://www.w3.org/2000/svg\"\n                                        role=\"img\" style=\"text-anchor: middle; user-select: none\">\n                                        <rect width=\"100%\" height=\"100%\" fill=\"#c6d3e6\"></rect>\n                                        <text x=\"50%\" y=\"50%\" fill=\"#869ab8\" dy=\".3em\">\n                                            暂无图片\n                                        </text>\n                                    </svg>\n                                    {%else%}\n                                    <img\n                    class=\"img-fluid rounded lazy\"\n                    data-src=\"{{item.cover_url}}\"\n                    src=\"data:image/gif;base64,R0lGODlhAQABAAAAACH5BAEKAAEALAAAAAABAAEAAAICTAEAOw==\"\n                    alt=\"...\"\n                  />\n                  {%endif%}\n                                </a>\n                            </figure>\n                        </div>\n                        <div class=\"col-12 mt-3 d-block d-sm-none\">\n                            <!-- mobile spacer -->\n                        </div>\n                        <div class=\"col-12 col-md-9 order-xs-3 order-md-2 order-lg-2 p-0 position-relative\">\n                            {%if item.level>0%}\n                            <span class=\"position-absolute top-0 end-0 mt-1\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"置顶[{{item.level}}]\">\n                <svg width=\"15\" viewBox=\"0 0 512 512\" class=\"fill-danger\"><path d=\"M141.938 324.965l-130.119 159.058c-3.387 4.146-3.517 10.24 0 14.535 4.002 4.918 11.247 5.646 16.147 1.625l159.107-130.131c-8.13-7.24-15.955-14.598-23.244-21.893-7.63-7.626-14.92-15.401-21.891-23.194zM447.89 64.1c-44.49-44.502-94.756-66.346-112.309-48.846-.903.908-92.757 126.197-138.796 188.98-30.166-21.5-57.219-34.082-74.656-34.068-6.374 0-11.485 1.662-14.971 5.18-19.197 19.164 16.646 86.076 80.028 149.459 51.797 51.797 105.971 85.223 134.488 85.223 6.387 0 11.472-1.68 14.971-5.178 13.084-13.084.548-48.41-28.858-89.658 62.802-46.043 188.047-137.893 188.932-138.781 17.535-17.518-4.339-67.804-48.829-112.311z\"></path></svg>\n              </span>\n                            {%endif%}\n                            <div class=\"p-0 p-lg-3\">\n                                <h3 class=\"h5 mb-2\">\n                                    <a href=\"/cms/detail/{{item.id}}\" target=\"_blank\"> {{item.title}} </a>\n                                </h3>\n\n                                <p class=\"mb-2\">\n                                    {{item.description|truncate(80, true, \"...\")}}\n                                </p>\n\n                                <ul class=\"list-inline text-gray-700 small mb-2\">\n                                    <li class=\"list-inline-item\">{{item.pathTitle}}</li>\n                                    <li class=\"list-inline-item\">/</li>\n                                    <li class=\"list-inline-item\">\n                                        {{helper.moment(item.createdAt).fromNow()}}\n                                    </li>\n                                    <li class=\"list-inline-item\">/</li>\n                                    <li class=\"list-inline-item\">{{item.view}}次</li>\n                                </ul>\n                            </div>\n                        </div>\n                    </div>\n                </div>\n                <!-- /item -->\n                {%endfor%}\n                <!-- pagination -->\n                <nav aria-label=\"pagination\" class=\"mt-4\">{{pagination|safe}}</nav>\n                <!-- pagination -->\n            </div>\n\n            <div class=\"col-12 col-lg-2 mb-3 order-1\">\n                <form method=\"get\" action=\"{{ classify.url }}\" class=\"input-group-over d-block mb-2\">\n                    <div class=\"form-floating mb-3\">\n                        <input\n              placeholder=\"搜索...\"\n              id=\"doc_search\"\n              name=\"keywords\"\n              type=\"text\"\n              class=\"form-control\"\n              value=\"{{ ctx.query.keywords }}\"\n            />\n            {% if ctx.query.sub %}\n                        <input name=\"sub\" type=\"hidden\" value=\"{{ ctx.query.sub }}\" />\n            {% endif %}\n                        <label for=\"doc_search\">搜索...</label>\n                    </div>\n\n                    <button type=\"submit\" class=\"btn smaller\">\n            <i class=\"fi fi-search m-0\"></i>\n          </button>\n                </form>\n                {%set classifyList = classify.id|@classify(\'top\')%} {%if classifyList%}\n                <!-- CATEGORIES -->\n                <nav\n                    class=\"nav-deep nav-deep-light mb-3 shadow-xs shadow-none-md shadow-none-xs px-3 pb-3 p-0-md p-0-xs rounded\">\n                    <!-- mobile trigger : categories -->\n                    <button\n            class=\"clearfix btn btn-toggle btn-sm w-100 text-align-left shadow-md border rounded mb-1 d-block d-lg-none\"\n            data-bs-target=\"#nav_responsive\"\n            data-toggle-container-class=\"d-none d-sm-block bg-white shadow-md border animate-fadein rounded p-3\"\n          >\n            <span class=\"group-icon px-2 py-2 float-start\">\n              <i class=\"fi fi-bars-2\"></i>\n              <i class=\"fi fi-close\"></i>\n            </span>\n            <span class=\"h5 py-2 m-0 float-start\"> 分类 </span>\n          </button>\n\n                    <!-- desktop only -->\n                    <h5 class=\"py-3 m-0 d-none d-lg-block\">分类</h5>\n\n                    <!-- navbar : navigation -->\n                    <ul id=\"nav_responsive\" class=\"nav flex-column d-none d-lg-block\">\n                        {%for item in classifyList%}\n                        <li\n                            class=\"nav-item {%if (item.id ==classify.id) or (classify.id in (item.arrId or [])) %}active{%endif%}\">\n                            {%if item.children%}\n                            <a class=\"nav-link px-0\" href=\"#\">\n                                <span class=\"group-icon\">\n                  <i class=\"fi fi-arrow-end\"></i>\n                  <i class=\"fi fi-arrow-down\"></i>\n                </span>\n                                <span class=\"px-2 d-inline-block\"> {{item.title}} </span>\n                            </a>\n                            {%else%}\n                            <a class=\"nav-link px-0\" href=\"{{item.url}}\">\n                                <i class=\"fi fi-arrow-end m-0 smaller\"></i>\n                                <span class=\"px-2 d-inline-block\"> {{item.title}} </span>\n                            </a>\n                            {%endif%} {%if item.children%}\n                            <ul class=\"nav flex-column ps-2\">\n                                {%for _item in item.children%}\n                                <li class=\"nav-item {%if _item.id ==classify.id%}active{%endif%}\">\n                                    <a class=\"nav-link\" href=\"{{_item.url}}\"> {{_item.title}} </a>\n                                </li>\n                                {%endfor%}\n                            </ul>\n                            {%endif%}\n                        </li>\n                        {%endfor%}\n                    </ul>\n                </nav>\n                <!-- /CATEGORIES -->\n                {%endif%} {% set subclassify = classify.id|@classify(\'sub\') %} {% for\n                item in subclassify%}\n                <!-- Suggestions -->\n                <div\n                    class=\"card border-0 shadow-xs d-block mb-3 px-3 pb-3 shadow-none-md shadow-none-xs p-0-md p-0-xs rounded\">\n                    <!-- mobile trigger : categories -->\n                    <button\n            class=\"clearfix btn btn-toggle btn-sm w-100 text-align-left shadow-md border rounded mb-1 d-block d-lg-none\"\n            data-bs-target=\"#nav_{{ item.name }}\"\n            data-toggle-container-class=\"d-none d-sm-block bg-white shadow-md border animate-fadein rounded p-3\"\n          >\n            <span class=\"group-icon px-2 py-2 float-start\">\n              <i class=\"fi fi-bars-2\"></i>\n              <i class=\"fi fi-close\"></i>\n            </span>\n            <span class=\"h5 py-2 m-0 float-start\"> {{ item.label }} </span>\n          </button>\n                    <!-- desktop only -->\n                    <h6 class=\"py-3 m-0 d-none d-lg-block\">{{ item.label }}</h6>\n                    {% if item.type==\'radios\' %}\n                    <div id=\"nav_{{ item.name }}\" class=\"checkgroup flex-column d-none d-lg-block classify-sub\"\n                        data-type=\"{{ item.type }}\" data-name=\"{{ item.name }}\" data-url=\"{{ item.url }}\"\n                        data-checkgroup-checkbox-single=\"true\">\n                        {% for v in item.options %}\n                        <div class=\"form-check mb-1\">\n                            <!-- item -->\n                            <input\n                class=\"form-check-input form-check-input-primary class-sub-input\"\n                name=\"{{ item.name }}\"\n                type=\"checkbox\"\n                value=\"{{ v.value }}\"\n                id=\"{{ item.name }}-{{ v.value }}\"\n                {%\n                if\n                v.check\n                %}checked{%\n                endif\n                %}\n              />\n                            <label\n                class=\"form-check-label\"\n                for=\"{{ item.name }}-{{ v.value }}\"\n              >\n                {{ v.label }}\n              </label>\n                        </div>\n                        {% endfor %}\n                    </div>\n                    {% elif item.type==\'checkboxes\' %}\n                    <div id=\"nav_{{ item.name }}\" class=\"checkgroup flex-column d-none d-lg-block\">\n                        <div class=\"form-check mb-2\">\n                            <!-- check all -->\n                            <input\n                data-checkall-container=\"#checkall-{{ item.name }}\"\n                class=\"form-check-input form-check-input-default class-sub-input\"\n                type=\"checkbox\"\n                value=\"\"\n                id=\"checkall-{{ item.name }}-top\"\n              />\n                            <label\n                class=\"form-check-label\"\n                for=\"checkall-{{ item.name }}-top\"\n              >\n                全选/不选\n              </label>\n                        </div>\n\n                        <div id=\"checkall-{{ item.name }}\" class=\"ps-0 classify-sub\" data-type=\"{{ item.type }}\"\n                            data-name=\"{{ item.name }}\" data-url=\"{{ item.url }}\">\n                            {% for v in item.options %}\n                            <div class=\"form-check mb-1\">\n                                <!-- item -->\n                                <input\n                  class=\"form-check-input form-check-input-primary class-sub-input\"\n                  name=\"{{ item.name }}\"\n                  type=\"checkbox\"\n                  value=\"{{ v.value }}\"\n                  id=\"{{ item.name }}-{{ v.value }}\"\n                  {%\n                  if\n                  v.check\n                  %}checked{%\n                  endif\n                  %}\n                />\n                                <label\n                  class=\"form-check-label\"\n                  for=\"{{ item.name }}-{{ v.value }}\"\n                >\n                  {{ v.label }}\n                </label>\n                            </div>\n                            {% endfor %}\n                        </div>\n                    </div>\n                    {% endif %}\n                </div>\n                <!-- /Suggestions -->\n                {% endfor %}\n                <!-- BANNER -->\n                <a href=\"#!\"\n                    class=\"mt-0 d-none d-lg-block text-center overlay-dark-hover overlay-opacity-2 rounded overflow-hidden\">\n                    <img class=\"w-100 img-fluid rounded\" src=\"/public/assets/demo.files/images/unsplash/portfolio/thumb_330/boxed-water-is-better-6RTh34xCS1M-unsplash-min.jpg\" alt=\"...\">\n                </a>\n                <h4 class=\"small text-gray-500 fw-normal mt-1 mb-0 d-none d-lg-block\">\n                    Sponsored Ad\n                </h4>\n                <!-- /BANNER -->\n            </div>\n        </div>\n    </div>\n</div>\n\n{% endblock %}\n{%block script%}\n<script src=\"/public/cms/{{\'cms\'|@templatePath}}/js/list.js\"></script>\n{%endblock%}', 1, 0, '6f181771-516b-40a4-9bd1-099d14b9d3d7');
INSERT INTO `cms_template_list` (`id`, `createdAt`, `updatedAt`, `template_uuid`, `title`, `type`, `name`, `html`, `isd`, `isu`, `uuid`) VALUES (3, '2024-10-07 04:48:18', '2024-10-07 04:48:18', '1f8dda2a-6e9e-4762-a273-1bc138ec9ffa', '默认详情模板', 'detail', 'default', '{% extends \"./inc_base.html\" %}\n{%block body%}class=\"header-scroll-reveal\"{%endblock%}\n{% block middle %}\n<!-- start :: blog content -->\n<div class=\"section pt-4\">\n    <div class=\"container\">\n\n        <div class=\"row g-xl-5\">\n\n            <div class=\"col-lg-9 order-2 order-lg-1\">\n\n                <div class=\"mb-4\">\n                    <nav aria-label=\"breadcrumb\">\n                        <ol class=\"breadcrumb small mb-3\">\n                            {%for item in breadcrumb%}\n                            <li class=\"breadcrumb-item\"><a href=\"{{item.url}}\">{{item.name}}</a></li>\n                            {%endfor%}\n                            <li class=\"breadcrumb-item\">详情</li>\n                        </ol>\n                    </nav>\n                    <h1 class=\"h3 mb-0\">\n                        {{detail.title}}\n                    </h1>\n                    <p class=\"d-block text-muted small\">\n                        发布时间: <time\n                            datetime=\"{{helper.moment(detail.createdAt).format(\'YYYY-MM-DD HH:mm:ss\')}}\">{{helper.moment(detail.createdAt).format(\'YYYY-MM-DD\n                            HH:mm:ss\')}}</time>\n                    </p>\n\n                </div>\n                {%if detail.models_uuid===\'e86401ba-85cb-47f7-9f53-853e26b939bd\'%}\n                <div class=\"article-format\">\n                    {{detail.cms_doc_article.content|safe}}\n                </div>\n                {%elif detail.models_uuid===\'4e0da60c-13af-4965-8f35-e2b13742398e\'%}\n                <!--\n						In order for lazyload to work, a height is required, else all images are in viewport.\n						By default, an empty png is set with 300px height:\n						http://png-pixel.com/\n					-->\n                <div class=\"row\">\n                    {% for item in detail.cms_doc_picture.content%}\n                    <div class=\"col-6 col-lg-4 mb-3\">\n\n                        <div class=\"bg-white p-2 shadow-primary-xs transition-hover-top transition-all-ease-250\">\n                            <a href=\"{{item.url}}\" data-fancybox=\"gallery\"\n                                class=\"fancybox fancybox-primary d-block overflow-hidden overlay-dark-hover overlay-opacity-2 text-decoration-none text-dark\">\n                                <img class=\"img-fluid lazy rounded\" data-src=\"{{item.url}}\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAEsCAQAAACoWRFeAAAAE0lEQVR42mNkYGAcRaNoFA0cAgAUvAEtNFICWAAAAABJRU5ErkJggg==\" alt=\"...\">\n                            </a>\n                            <div class=\"p-2\">\n                                <h6 class=\"m-0\">\n                                    {{item.desc}}\n                                </h6>\n                            </div>\n                        </div>\n\n                    </div>\n                    {%endfor%}\n                </div>\n\n                {%elif detail.models_uuid===\'aac4b5a3-89f2-4c41-9213-39f43dcc0860\'%}\n\n                <div class=\"row\">\n                    <div class=\"col mb-3\">\n                        {%if detail.cover_url%}\n                        <figure class=\"mb-3\">\n                            <img class=\"img-fluid\" src=\"{{detail.cover_url}}\" alt=\"...\">\n                        </figure>\n                        {%endif%}\n                        {%if detail.cms_doc_download.desc%}\n                        {{detail.cms_doc_download.desc|safe}}\n                        {%endif%}\n                    </div>\n                    <div class=\"col-12 col-lg-3 mb-3 \">\n                        <!-- specifications -->\n                        <div class=\"sticky-kit z-index-0 bg-w\">\n                            <div class=\"card\">\n                                <div class=\"card-body\">\n                                    <h3 class=\"h5\">\n                                        下载列表\n                                    </h3>\n\n                                    <!-- specs list -->\n                                    <ul class=\"list-unstyled mb-0\">\n                                        {%for item in detail.cms_doc_download.content%}\n                                        <li class=\"list-item clearfix border-bottom py-2\">\n                                            <a href=\"#\" data-bs-toggle=\"modal\" data-bs-target=\"#d-{{loop.index}}\">\n                                                <i class=\"fi {%if item.d_type===\'1\'%}fi-arrow-download{%else%}fi-cloud-download{%endif%} text-gray-400 fs-5\"></i>\n                                                <span class=\"d-inline-block px-2\">{{item.d_title}}</span>\n                                            </a>\n                                        </li>\n                                        {%endfor%}\n                                    </ul>\n                                    <!-- /specs list -->\n                                </div>\n                            </div>\n                        </div>\n                        {%for item in detail.cms_doc_download.content%}\n                        <!-- Modal -->\n                        <div class=\"modal fade\" id=\"d-{{loop.index}}\" data-bs-keyboard=\"false\" tabindex=\"-1\"\n                            aria-labelledby=\"modalCenteredLabel\" aria-hidden=\"true\">\n                            <div class=\"modal-dialog modal-dialog-centered\">\n                                <div class=\"modal-content\">\n\n                                    <div class=\"modal-body\">\n                                        {%if item.d_password%}\n                                        <h6>\n                                            密码：<code>{{item.d_password}}</code>\n                                        </h6>\n                                        {%endif%}\n                                        <div class=\"alert alert-primary mb-0\" role=\"alert\">\n                                            点击下载无响应？ 是因为浏览器阻止了弹出窗口，网址输入框的最右边有个图标，点击后允许即可！\n                                        </div>\n                                    </div>\n\n                                    <div class=\"modal-footer\">\n                                        <button type=\"button\" class=\"btn btn-secondary\" data-bs-dismiss=\"modal\">关闭</button>\n                                        <a href=\"{{item.d_url}}\" type=\"button\" class=\"btn btn-primary\"\n                                            target=\"_blank\">点击下载</a>\n                                    </div>\n                                </div>\n                            </div>\n                        </div>\n                        {%endfor%}\n                        <!-- /specifications -->\n                        {% if detail.description %}\n                        <div\n                            class=\"p-2 mt-3 bg-primary-soft border-start border-end border-primary border-2 text-white rounded-xl\">\n                            <h3 class=\"h6\">简介</h3>\n                            <div class=\"small\">\n                                {{detail.description}}\n                            </div>\n                        </div>\n                        {%endif%}\n\n                    </div>\n\n\n                </div>\n\n                {%endif%}\n                <p class=\"text-muted border-bottom pb-2 small mt-5\">\n                    最后更新时间: {{helper.moment(detail.updatedAt).format(\'YYYY-MM-DD HH:mm:ss\')}}\n                </p>\n                {%set tags = detail.tags|split(\',\')%}\n                {%if tags%}\n                <!-- Tags and share -->\n                <div class=\"row\">\n                    <div class=\"col-12\">\n\n                        <h4 class=\"smaller text-muted\">\n                            相关标签\n                        </h4>\n                        {%for item in tags%}\n                        <a href=\"#!\" class=\"text-dark bg-light small rounded py-2 px-3 m-1 text-decoration-none\"\n                            rel=\"nofollow\">\n                            {{item}}\n                        </a>\n                        {%endfor%}\n                    </div>\n                </div>\n                <!-- /Tags and share -->\n                {%endif%}\n\n\n\n                <!-- COMMENTS -->\n                {%if detail.cms_classify.reply%}\n                {% include \"./comment_index.html\" %}\n                {%endif%}\n                <!-- /COMMENTS -->\n\n\n\n\n\n\n\n            </div>\n\n            <div class=\"col-lg-3 order-1 order-lg-2 mb-5 d-none d-lg-block\">\n\n\n                <!-- SEARCH -->\n                <form method=\"get\" action=\"{{detail.classify_url}}\" class=\"input-group-over d-block mb-2\">\n\n                    <div class=\"form-floating mb-3\">\n                        <input required placeholder=\"内容搜索...\" id=\"blog_search\" name=\"keywords\" type=\"text\" class=\"form-control\">\n                        <label for=\"blog_search\">内容搜索...</label>\n                    </div>\n\n                    <button type=\"submit\" class=\"btn smaller\">\n									<i class=\"fi fi-search m-0\"></i>\n								</button>\n\n                </form>\n                <!-- /SEARCH -->\n\n\n\n                {%set classifyList = detail.classify_id|@classify(\'top\')%} {%if classifyList%}\n                <!-- CATEGORIES -->\n                <nav\n                    class=\"nav-deep nav-deep-light mb-3 shadow-xs shadow-none-md shadow-none-xs px-3 pb-3 p-0-md p-0-xs rounded\">\n                    <!-- mobile trigger : categories -->\n                    <button\n            class=\"clearfix btn btn-toggle btn-sm w-100 text-align-left shadow-md border rounded mb-1 d-block d-lg-none\"\n            data-bs-target=\"#nav_responsive\"\n            data-toggle-container-class=\"d-none d-sm-block bg-white shadow-md border animate-fadein rounded p-3\"\n          >\n            <span class=\"group-icon px-2 py-2 float-start\">\n              <i class=\"fi fi-bars-2\"></i>\n              <i class=\"fi fi-close\"></i>\n            </span>\n            <span class=\"h5 py-2 m-0 float-start\"> 分类 </span>\n          </button>\n\n                    <!-- desktop only -->\n                    <h5 class=\"py-3 m-0 d-none d-lg-block\">分类</h5>\n\n                    <!-- navbar : navigation -->\n                    <ul id=\"nav_responsive\" class=\"nav flex-column d-none d-lg-block\">\n                        {%for item in classifyList%}\n                        <li\n                            class=\"nav-item {%if (item.id ==classify.id) or (classify.id in (item.arrId or [])) %}active{%endif%}\">\n                            {%if item.children%}\n                            <a class=\"nav-link px-0\" href=\"#\">\n                                <span class=\"group-icon\">\n                  <i class=\"fi fi-arrow-end\"></i>\n                  <i class=\"fi fi-arrow-down\"></i>\n                </span>\n                                <span class=\"px-2 d-inline-block\"> {{item.title}} </span>\n                            </a>\n                            {%else%}\n                            <a class=\"nav-link px-0\" href=\"{{item.url}}\">\n                                <i class=\"fi fi-arrow-end m-0 smaller\"></i>\n                                <span class=\"px-2 d-inline-block\"> {{item.title}} </span>\n                            </a>\n                            {%endif%} {%if item.children%}\n                            <ul class=\"nav flex-column ps-2\">\n                                {%for _item in item.children%}\n                                <li class=\"nav-item {%if _item.id ==classify.id%}active{%endif%}\">\n                                    <a class=\"nav-link\" href=\"{{_item.url}}\"> {{_item.title}} </a>\n                                </li>\n                                {%endfor%}\n                            </ul>\n                            {%endif%}\n                        </li>\n                        {%endfor%}\n                    </ul>\n                </nav>\n                <!-- /CATEGORIES -->\n                {%endif%}\n                {% set hotlist = \'cms_doc\'|@findAll({\n                where:{\n                classify_id:detail.classify_id\n                },\n                offset: 0,\n                limit: 5,\n                order:[[\'view\',\'desc\']]\n                })%}\n                {%if hotlist%}\n                <div class=\"d-none d-lg-block\">\n                    <h3 class=\"h5 mt-5 mt-0-xs\">热点</h3>\n\n                    <ul class=\"list-unstyled mb-0\">\n                        {%for item in hotlist%}\n                        <li class=\"list-item d-flex align-items-center position-relative mt-4\">\n                            <div class=\"ratio ratio-16x9 bg-light me-3\" style=\"max-width: 50px;\">\n                                <div class=\"bg-cover rounded\" style=\"background-image:url({{item.cover_url}});\"></div>\n                            </div>\n                            <a href=\"/cms/detail/{{item.id}}\"\n                                class=\"link-normal small stretched-link\">{{item.title|truncate(20, true, \"...\")}}</a>\n                        </li>\n                        {%endfor%}\n                    </ul>\n\n                </div>\n                {%endif%}\n                <!-- mobile trigger : expand -->\n\n                <button class=\"mt-3 clearfix btn btn-toggle btn-sm w-100 text-align-left shadow-md border rounded mb-1 d-block d-lg-none\" data-bs-target=\"#sidebar_expand_mobile\" data-toggle-container-class=\"d-none d-sm-block bg-white shadow-md border animate-fadein rounded p-3\">\n								<span class=\"group-icon px-2 py-2 float-start\">\n									<i class=\"fi fi-bars-2\"></i>\n									<i class=\"fi fi-close\"></i>\n								</span>\n\n								<span class=\"h5 py-2 m-0 float-start \">\n									Explore\n								</span>\n							</button>\n\n                <div id=\"sidebar_expand_mobile\" class=\"d-none d-lg-block\">\n                    <!-- BANNER -->\n                    <a href=\"#!\"\n                        class=\"mt-3 d-block text-center overlay-dark-hover overlay-opacity-2 rounded overflow-hidden\">\n                        <img class=\"w-100 img-fluid rounded\" src=\"/public/assets/demo.files/images/unsplash/portfolio/thumb_330/boxed-water-is-better-6RTh34xCS1M-unsplash-min.jpg\" alt=\"...\">\n                    </a>\n                    <h4 class=\"small text-gray-500 fw-normal mt-1 mb-0\">\n                        Sponsored Ad\n                    </h4>\n                    <!-- /BANNER -->\n\n                </div>\n\n            </div>\n\n        </div>\n\n    </div>\n</div>\n<!-- end :: blog content -->\n\n{% endblock %}\n{%block script%}\n<script>\n    //评论\n  var cms_comments_callback = function(from,data){\n		if(data.status===1000){\n          $.SOW.core.toast.show(\'danger\', \'Error\', data.msg, \'top-end\');\n        }else{\n          $.SOW.core.toast.show(\'success\', \'Success\', data.msg, \'top-end\');\n          $(from).find(\'[name=\"content\"]\').empty()\n					$.SOW.core.ajax_content.__ajaxDivProcess(\'cms_comments_container\', \'/cms/comments/ajaxList?doc_id={{detail.id}}\', \'#cms_comments_container\',$.SOW.core.ajax_content.config.method,null,$.SOW.core.ajax_content.config.callback_function,$.SOW.core.ajax_content.config.callback_before_push);\n        }\n	}\n	var cms_comments_reply_callback = function(from,data){\n		if(data.status===1000){\n          $.SOW.core.toast.show(\'danger\', \'Error\', data.msg, \'top-end\');\n        }else{\n          $.SOW.core.toast.show(\'success\', \'Success\', data.msg, \'top-end\');\n          $(from).find(\'[name=\"content\"]\').empty()\n					$(\'#sow_ajax_modal\').modal(\'hide\');\n					$.SOW.core.ajax_content.__ajaxDivProcess(\'cms_comments_container\', \'/cms/comments/ajaxList?doc_id={{detail.id}}&page=\'+data.data, \'#cms_comments_container\',$.SOW.core.ajax_content.config.method,null,$.SOW.core.ajax_content.config.callback_function,$.SOW.core.ajax_content.config.callback_before_push);\n        }\n	 }\n</script>\n{%endblock%}', 1, 0, '850f07f4-be83-4def-ad1e-545bfa5bfb2a');
INSERT INTO `cms_template_list` (`id`, `createdAt`, `updatedAt`, `template_uuid`, `title`, `type`, `name`, `html`, `isd`, `isu`, `uuid`) VALUES (4, '2024-10-07 04:48:18', '2024-10-07 04:48:18', '1f8dda2a-6e9e-4762-a273-1bc138ec9ffa', '默认公共基础模板', 'inc', 'base', '<!doctype html>\n<html lang=\"zh-CN\" xmlns=\"http://www.w3.org/1999/xhtml\">\n\n<head>\n    <meta charset=\"UTF-8\">\n    <title>{{ctx.meta_title}}-{{ctx.app.config.sys.name}}</title>\n    <meta name=\"keywords\" content=\"{{ctx.keywords}}\" />\n    <meta name=\"description\" content=\"{{ctx.description}}\" />\n    <meta name=\"viewport\" content=\"width=device-width, maximum-scale=5, initial-scale=1\">\n    <!--[if IE]><meta http-equiv=\'X-UA-Compatible\' content=\'IE=edge,chrome=1\'><![endif]-->\n\n    <!-- preloading icon font is helping to speed up a little bit -->\n    <link rel=\"preload\" href=\"/public/assets/fonts/flaticon/Flaticon.woff2\" as=\"font\" type=\"font/woff2\" crossorigin>\n    <link rel=\"stylesheet\" href=\"/public/assets/css/core.min.css\">\n    <link rel=\"stylesheet\" href=\"/public/assets/css/vendor_bundle.min.css\">\n\n\n    <link rel=\"stylesheet\" href=\"/public/cms/{{\'cms\'|@templatePath}}/css/app.css\">\n    <!-- favicon -->\n    <link rel=\"shortcut icon\" href=\"/public/cms/{{\'cms\'|@templatePath}}/favicon.ico\">\n    <link rel=\"apple-touch-icon\" href=\"/public/cms/{{\'cms\'|@templatePath}}/logo/icon_512x512.png\">\n\n</head>\n\n<body {%block body%}{%endblock%}>\n    <!-- wrapper -->\n    <div id=\"wrapper\">\n        {# 网站头部 #}\n        {% include \"./inc_header.html\" %}\n        {# 网站头部结束 #}\n\n        {# 网站内容 #}\n        {% block middle %}{% endblock %}\n        {# 网站内容结束 #}\n\n        {# 网站底部 #}\n        {% include \"./inc_footer.html\" %}\n        {# 网站底部结束 #}\n    </div>\n    <!-- /#wrapper -->\n    <script src=\"/public/assets/js/core.min.js\"></script>\n    <div id=\"page_js_files\">\n        <script>\n            //登录\n        var mc_sign_callback = function(form,data){\n          if(data.status===1000){\n            $.SOW.core.toast.show(\'danger\', \'Error\', data.msg, \'top-end\');\n          }else{\n            $.SOW.core.toast.show(\'success\', \'Success\', data.msg, \'top-end\');\n            location.reload();\n          }\n        }\n        //注册\n        var mc_signup_callback = function(form,data){\n        if(data.status===1000){\n          $.SOW.core.toast.show(\'danger\', \'Error\', data.msg, \'top-end\');\n        }else{\n          $.SOW.core.toast.show(\'success\', \'Success\', data.msg, \'top-end\');\n          $(form)[0].reset();\n          $(\"#accordionAccountSignUp\").removeClass(\'show\');\n          $(\"#accordionAccountSignIn\").addClass(\'show\')\n        }\n        }\n        </script>\n        <!-- additional javascripts inside here -->\n        {%block script%}{%endblock%}\n    </div>\n\n</body>\n\n</html>', 1, 0, 'd6fe605d-f944-40db-8397-24c56cf95a7b');
