INSERT INTO `cms_template_list` (`id`, `createdAt`, `updatedAt`, `template_uuid`, `title`, `type`, `name`, `html`, `isd`, `isu`, `uuid`) VALUES (23, '2024-10-07 04:48:18', '2024-10-07 04:48:18', '37934d72-fe2c-4a0d-afbb-414993e63986', '文档列表模板', 'list', 'documentation', '{% extends \"./inc_base.html\" %}\n{% block middle %}\n{% set detail = \'cms_doc\'|@findOne({\ninclude : \'cms_doc_article\',\nwhere:{classify_id:classify.id,pid:0,status:1},order:[[ \'sort\', \'ASC\' ]]\n})%}\n<div class=\"section pt-4 pb-4\">\n    <div class=\"container\">\n        <div class=\"row g-xl-5\">\n            <div class=\"col-12 col-lg-9 order-2\">\n                <div class=\"mb-4\">\n                    <nav aria-label=\"breadcrumb\">\n                        <ol class=\"breadcrumb small mb-3\">\n                            {%for item in breadcrumb%}\n                            <li class=\"breadcrumb-item\"><a href=\"{{item.url}}\">{{item.name}}</a></li>\n                            {%endfor%}\n                            <li class=\"breadcrumb-item\">详情</li>\n                        </ol>\n                    </nav>\n                    <h1 class=\"h3 mb-0\">\n                        {{detail.title}}\n                    </h1>\n                    <p class=\"d-block text-muted small\">\n                        发布时间: <time\n                            datetime=\"{{helper.moment(detail.createdAt).format(\'YYYY-MM-DD HH:mm:ss\')}}\">{{helper.moment(detail.createdAt).format(\'YYYY-MM-DD\n                            HH:mm:ss\')}}</time>\n                    </p>\n                </div>\n                <div class=\"article-format\" data-id=\"{{detail.id}}\">\n                    {{detail.cms_doc_article.content|safe}}\n                </div>\n                <p class=\"text-muted border-bottom pb-2 small mt-5\">\n                    最后更新时间: {{helper.moment(detail.updatedAt).format(\'YYYY-MM-DD HH:mm:ss\')}}\n                </p>\n                {%set tags = detail.tags|split(\',\')%}\n                {%if tags%}\n                <!-- Tags and share -->\n                <div class=\"row\">\n                    <div class=\"col-12\">\n\n                        <h4 class=\"smaller text-muted\">\n                            相关标签\n                        </h4>\n                        {%for item in tags%}\n                        <a href=\"#!\" class=\"text-dark bg-light small rounded py-2 px-3 m-1 text-decoration-none\"\n                            rel=\"nofollow\">\n                            {{item}}\n                        </a>\n                        {%endfor%}\n                    </div>\n                </div>\n                <!-- /Tags and share -->\n                {%endif%}\n                <!-- COMMENTS -->\n                {%if classify.reply%}\n                {% include \"./comment_index.html\" %}\n                {%endif%}\n                <!-- /COMMENTS -->\n            </div>\n\n            <div class=\"col-12 col-lg-3 mb-3 order-1\">\n                <form method=\"get\" action=\"{{ classify.url }}\" class=\"input-group-over d-block mb-2\">\n                    <div class=\"form-floating mb-3\">\n                        <input\n              placeholder=\"搜索...\"\n              id=\"doc_search\"\n              name=\"keywords\"\n              type=\"text\"\n              class=\"form-control\"\n              value=\"{{ ctx.query.keywords }}\"\n            />\n            {% if ctx.query.sub %}\n                        <input name=\"sub\" type=\"hidden\" value=\"{{ ctx.query.sub }}\" />\n            {% endif %}\n                        <label for=\"doc_search\">搜索...</label>\n                    </div>\n\n                    <button type=\"submit\" class=\"btn smaller\">\n            <i class=\"fi fi-search m-0\"></i>\n          </button>\n                </form>\n\n                <!-- CATEGORIES -->\n                <nav\n                    class=\"sticky-kit z-index-0 nav-deep nav-deep-light nav-deep-hover nav-deep-xs mb-3 pb-3 shadow-xs shadow-none-md shadow-none-xs p-0-md p-0-xs rounded\">\n                    <!-- mobile trigger : categories -->\n                    <button\n            class=\"clearfix btn btn-toggle btn-sm w-100 text-align-left shadow-md border rounded mb-1 d-block d-lg-none\"\n            data-bs-target=\"#nav_responsive\"\n            data-toggle-container-class=\"d-none d-sm-block bg-white shadow-md border animate-fadein rounded\"\n          >\n            <span class=\"group-icon px-2 py-2 float-start\">\n              <i class=\"fi fi-bars-2\"></i>\n              <i class=\"fi fi-close\"></i>\n            </span>\n            <span class=\"h5 py-2 m-0 float-start\"> 目录 </span>\n          </button>\n\n                    <!-- desktop only -->\n                    <h5 class=\"p-3 m-0 d-none d-lg-block\">目录</h5>\n\n                    <!-- navbar : navigation -->\n                    <ul id=\"nav_responsive\" class=\"nav flex-column d-none d-lg-block\">\n                        {%set nav = \'cms_doc\'|@findAll({where:{classify_id:classify.id,pid:0,status:1},order:[[ \'sort\',\n                        \'ASC\' ]]})%}\n                        {%for n in nav%}\n                        {%set _nav = \'cms_doc\'|@findAll({where:{classify_id:classify.id,pid:n.id,status:1},order:[[\n                        \'sort\', \'ASC\' ]]})%}\n                        {%if _nav|length>0%}\n                        <li class=\"nav-item\">\n                            <a class=\"nav-link\" href=\"#\">\n                                <span class=\"group-icon float-end\">\n                  <i class=\"fi fi-arrow-end-slim\"></i>\n                  <i class=\"fi fi-arrow-down-slim\"></i>\n                </span>\n                                {{n.title}}\n                            </a>\n                            <ul class=\"nav flex-column ps-3\">\n                                {%for _n in _nav%}\n                                {%set __nav =\n                                \'cms_doc\'|@findAll({where:{classify_id:classify.id,pid:_n.id,status:1},order:[[ \'sort\',\n                                \'ASC\' ]]})%}\n                                {%if __nav|length>0%}\n                                <li class=\"nav-item\">\n                                    <a class=\"nav-link\" href=\"#\">\n                                        <span class=\"group-icon float-end\">\n                      <i class=\"fi fi-arrow-end-slim\"></i>\n                      <i class=\"fi fi-arrow-down-slim\"></i>\n                    </span>\n                                        {{_n.title}}\n                                    </a>\n                                    <ul class=\"nav flex-column\">\n                                        {%for __n in __nav%}\n                                        <li class=\"nav-item\"><a class=\"nav-link\"\n                                                href=\"/cms/detail/{{__n.id}}\">{{__n.title}}</a></li>\n                                        {%endfor%}\n                                    </ul>\n                                </li>\n                                {%else%}\n                                <li class=\"nav-item\"><a class=\"nav-link\" href=\"/cms/detail/{{_n.id}}\">{{_n.title}}</a>\n                                </li>\n                                {%endif%}\n                                {%endfor%}\n                            </ul>\n                        </li>\n                        {%else%}\n                        <li class=\"nav-item\"><a class=\"nav-link\" href=\"/cms/detail/{{n.id}}\">{{n.title}}</a></li>\n                        {%endif%}\n                        {%endfor%}\n                    </ul>\n                </nav>\n                <!-- /CATEGORIES -->\n\n            </div>\n        </div>\n    </div>\n</div>\n\n{% endblock %}\n{%block script%}\n<script>\n    var id = $(\'.article-format\').data(\'id\');\n  //评论\n  var cms_comments_callback = function(from,data){\n		if(data.status===1000){\n          $.SOW.core.toast.show(\'danger\', \'Error\', data.msg, \'top-end\');\n        }else{\n          $.SOW.core.toast.show(\'success\', \'Success\', data.msg, \'top-end\');\n          $(from).find(\'[name=\"content\"]\').empty()\n					$.SOW.core.ajax_content.__ajaxDivProcess(\'cms_comments_container\', \'/cms/comments/ajaxList?doc_id=\'+id, \'#cms_comments_container\',$.SOW.core.ajax_content.config.method,null,$.SOW.core.ajax_content.config.callback_function,$.SOW.core.ajax_content.config.callback_before_push);\n        }\n	}\n	var cms_comments_reply_callback = function(from,data){\n		if(data.status===1000){\n          $.SOW.core.toast.show(\'danger\', \'Error\', data.msg, \'top-end\');\n        }else{\n          $.SOW.core.toast.show(\'success\', \'Success\', data.msg, \'top-end\');\n          $(from).find(\'[name=\"content\"]\').empty()\n					$(\'#sow_ajax_modal\').modal(\'hide\');\n					$.SOW.core.ajax_content.__ajaxDivProcess(\'cms_comments_container\', \'/cms/comments/ajaxList?doc_id=\'+id+\'&page=\'+data.data, \'#cms_comments_container\',$.SOW.core.ajax_content.config.method,null,$.SOW.core.ajax_content.config.callback_function,$.SOW.core.ajax_content.config.callback_before_push);\n        }\n	 }\n</script>\n{%endblock%}', 0, 0, '173d6bd2-8d4e-4f73-a496-579439a1ba65');
INSERT INTO `cms_template_list` (`id`, `createdAt`, `updatedAt`, `template_uuid`, `title`, `type`, `name`, `html`, `isd`, `isu`, `uuid`) VALUES (24, '2024-10-07 04:48:18', '2024-10-07 04:48:18', '37934d72-fe2c-4a0d-afbb-414993e63986', '文档详情模板', 'detail', 'documentation', '{% extends \"./inc_base.html\" %}\n{% block middle %}\n<!-- start :: blog content -->\n<div class=\"section pt-4\">\n    <div class=\"container\">\n\n        <div class=\"row g-xl-5\">\n\n            <div class=\"col-lg-9 order-2\">\n\n                <div class=\"mb-4\">\n                    <nav aria-label=\"breadcrumb\">\n                        <ol class=\"breadcrumb small mb-3\">\n                            {%for item in breadcrumb%}\n                            <li class=\"breadcrumb-item\"><a href=\"{{item.url}}\">{{item.name}}</a></li>\n                            {%endfor%}\n                            <li class=\"breadcrumb-item\">详情</li>\n                        </ol>\n                    </nav>\n                    <h1 class=\"h3 mb-0\">\n                        {{detail.title}}\n                    </h1>\n                    <p class=\"d-block text-muted small\">\n                        发布时间: <time\n                            datetime=\"{{helper.moment(detail.createdAt).format(\'YYYY-MM-DD HH:mm:ss\')}}\">{{helper.moment(detail.createdAt).format(\'YYYY-MM-DD\n                            HH:mm:ss\')}}</time>\n                    </p>\n\n                </div>\n                {%if detail.models_uuid===\'e86401ba-85cb-47f7-9f53-853e26b939bd\'%}\n                <div class=\"article-format\">\n                    {{detail.cms_doc_article.content|safe}}\n                </div>\n                {%elif detail.models_uuid===\'4e0da60c-13af-4965-8f35-e2b13742398e\'%}\n                <!--\n						In order for lazyload to work, a height is required, else all images are in viewport.\n						By default, an empty png is set with 300px height:\n						http://png-pixel.com/\n					-->\n                <div class=\"row\">\n                    {% for item in detail.cms_doc_picture.content%}\n                    <div class=\"col-6 col-lg-4 mb-3\">\n\n                        <div class=\"bg-white p-2 shadow-primary-xs transition-hover-top transition-all-ease-250\">\n                            <a href=\"{{item.url}}\" data-fancybox=\"gallery\"\n                                class=\"fancybox fancybox-primary d-block overflow-hidden overlay-dark-hover overlay-opacity-2 text-decoration-none text-dark\">\n                                <img class=\"img-fluid lazy rounded\" data-src=\"{{item.url}}\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAEsCAQAAACoWRFeAAAAE0lEQVR42mNkYGAcRaNoFA0cAgAUvAEtNFICWAAAAABJRU5ErkJggg==\" alt=\"...\">\n                            </a>\n                            <div class=\"p-2\">\n                                <h6 class=\"m-0\">\n                                    {{item.desc}}\n                                </h6>\n                            </div>\n                        </div>\n\n                    </div>\n                    {%endfor%}\n                </div>\n\n                {%elif detail.models_uuid===\'aac4b5a3-89f2-4c41-9213-39f43dcc0860\'%}\n\n                <div class=\"row\">\n                    <div class=\"col mb-3\">\n                        {%if detail.cover_url%}\n                        <figure class=\"mb-3\">\n                            <img class=\"img-fluid\" src=\"{{detail.cover_url}}\" alt=\"...\">\n                        </figure>\n                        {%endif%}\n                        {%if detail.cms_doc_download.desc%}\n                        {{detail.cms_doc_download.desc|safe}}\n                        {%endif%}\n                    </div>\n                    <div class=\"col-12 col-lg-3 mb-3 \">\n                        <!-- specifications -->\n                        <div class=\"sticky-kit z-index-0 bg-w\">\n                            <div class=\"card\">\n                                <div class=\"card-body\">\n                                    <h3 class=\"h5\">\n                                        下载列表\n                                    </h3>\n\n                                    <!-- specs list -->\n                                    <ul class=\"list-unstyled mb-0\">\n                                        {%for item in detail.cms_doc_download.content%}\n                                        <li class=\"list-item clearfix border-bottom py-2\">\n                                            <a href=\"#\" data-bs-toggle=\"modal\" data-bs-target=\"#d-{{loop.index}}\">\n                                                <i class=\"fi {%if item.d_type===\'1\'%}fi-arrow-download{%else%}fi-cloud-download{%endif%} text-gray-400 fs-5\"></i>\n                                                <span class=\"d-inline-block px-2\">{{item.d_title}}</span>\n                                            </a>\n                                        </li>\n                                        {%endfor%}\n                                    </ul>\n                                    <!-- /specs list -->\n                                </div>\n                            </div>\n                        </div>\n                        {%for item in detail.cms_doc_download.content%}\n                        <!-- Modal -->\n                        <div class=\"modal fade\" id=\"d-{{loop.index}}\" data-bs-keyboard=\"false\" tabindex=\"-1\"\n                            aria-labelledby=\"modalCenteredLabel\" aria-hidden=\"true\">\n                            <div class=\"modal-dialog modal-dialog-centered\">\n                                <div class=\"modal-content\">\n\n                                    <div class=\"modal-body\">\n                                        {%if item.d_password%}\n                                        <h6>\n                                            密码：<code>{{item.d_password}}</code>\n                                        </h6>\n                                        {%endif%}\n                                        <div class=\"alert alert-primary mb-0\" role=\"alert\">\n                                            点击下载无响应？ 是因为浏览器阻止了弹出窗口，网址输入框的最右边有个图标，点击后允许即可！\n                                        </div>\n                                    </div>\n\n                                    <div class=\"modal-footer\">\n                                        <button type=\"button\" class=\"btn btn-secondary\" data-bs-dismiss=\"modal\">关闭</button>\n                                        <a href=\"{{item.d_url}}\" type=\"button\" class=\"btn btn-primary\"\n                                            target=\"_blank\">点击下载</a>\n                                    </div>\n                                </div>\n                            </div>\n                        </div>\n                        {%endfor%}\n                        <!-- /specifications -->\n                        {% if detail.description %}\n                        <div\n                            class=\"p-2 mt-3 bg-primary-soft border-start border-end border-primary border-2 text-white rounded-xl\">\n                            <h3 class=\"h6\">简介</h3>\n                            <div class=\"small\">\n                                {{detail.description}}\n                            </div>\n                        </div>\n                        {%endif%}\n\n                    </div>\n\n\n                </div>\n\n                {%endif%}\n                <p class=\"text-muted border-bottom pb-2 small mt-5\">\n                    最后更新时间: {{helper.moment(detail.updatedAt).format(\'YYYY-MM-DD HH:mm:ss\')}}\n                </p>\n                {%set tags = detail.tags|split(\',\')%}\n                {%if tags%}\n                <!-- Tags and share -->\n                <div class=\"row\">\n                    <div class=\"col-12\">\n\n                        <h4 class=\"smaller text-muted\">\n                            相关标签\n                        </h4>\n                        {%for item in tags%}\n                        <a href=\"#!\" class=\"text-dark bg-light small rounded py-2 px-3 m-1 text-decoration-none\"\n                            rel=\"nofollow\">\n                            {{item}}\n                        </a>\n                        {%endfor%}\n                    </div>\n                </div>\n                <!-- /Tags and share -->\n                {%endif%}\n\n\n\n                <!-- COMMENTS -->\n                {%if detail.cms_classify.reply%}\n                {% include \"./comment_index.html\" %}\n                {%endif%}\n                <!-- /COMMENTS -->\n\n\n\n\n\n\n\n            </div>\n\n            <div class=\"col-lg-3 order-1 mb-3\">\n                <!-- SEARCH -->\n                <form method=\"get\" action=\"{{detail.classify_url}}\" class=\"input-group-over d-block mb-2\">\n\n                    <div class=\"form-floating mb-3\">\n                        <input required placeholder=\"内容搜索...\" id=\"blog_search\" name=\"keywords\" type=\"text\" class=\"form-control\">\n                        <label for=\"blog_search\">内容搜索...</label>\n                    </div>\n\n                    <button type=\"submit\" class=\"btn smaller\">\n									<i class=\"fi fi-search m-0\"></i>\n								</button>\n\n                </form>\n                <!-- /SEARCH -->\n                <!-- CATEGORIES -->\n                <nav\n                    class=\"sticky-kit z-index-0 nav-deep nav-deep-light nav-deep-hover nav-deep-xs mb-3 pb-3 shadow-xs shadow-none-md shadow-none-xs p-0-md p-0-xs rounded\">\n                    <!-- mobile trigger : categories -->\n                    <button\n	class=\"clearfix btn btn-toggle btn-sm w-100 text-align-left shadow-md border rounded mb-1 d-block d-lg-none\"\n	data-bs-target=\"#nav_responsive\"\n	data-toggle-container-class=\"d-none d-sm-block bg-white shadow-md border animate-fadein rounded\"\n>\n	<span class=\"group-icon px-2 py-2 float-start\">\n		<i class=\"fi fi-bars-2\"></i>\n		<i class=\"fi fi-close\"></i>\n	</span>\n	<span class=\"h5 py-2 m-0 float-start\"> 目录 </span>\n</button>\n\n                    <!-- desktop only -->\n                    <h5 class=\"p-3 m-0 d-none d-lg-block\">目录</h5>\n\n                    <!-- navbar : navigation -->\n                    <ul id=\"nav_responsive\" class=\"nav flex-column d-none d-lg-block\">\n                        {%set nav =\n                        \'cms_doc\'|@findAll({where:{classify_id:detail.cms_classify.id,pid:0,status:1},order:[[ \'sort\',\n                        \'ASC\' ]]})%}\n                        {%for n in nav%}\n                        {%set _nav =\n                        \'cms_doc\'|@findAll({where:{classify_id:detail.cms_classify.id,pid:n.id,status:1},order:[[\n                        \'sort\', \'ASC\' ]]})%}\n                        {%if _nav|length>0%}\n                        <li class=\"nav-item {%if (n.id in (pdocids or []))%}active{%endif%}\">\n                            <a class=\"nav-link\" href=\"#\">\n                                <span class=\"group-icon float-end\">\n				<i class=\"fi fi-arrow-end-slim\"></i>\n				<i class=\"fi fi-arrow-down-slim\"></i>\n			</span>\n                                {{n.title}}\n                            </a>\n                            <ul class=\"nav flex-column ps-3\">\n                                {%for _n in _nav%}\n                                {%set __nav =\n                                \'cms_doc\'|@findAll({where:{classify_id:detail.cms_classify.id,pid:_n.id,status:1},order:[[\n                                \'sort\', \'ASC\' ]]})%}\n                                {%if __nav|length>0%}\n                                <li class=\"nav-item {%if (_n.id in (pdocids or []))%}active{%endif%}\">\n                                    <a class=\"nav-link\" href=\"#\">\n                                        <span cla